    //Equals
    private void jBtnEqualsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBtnEqualsActionPerformed
        String answer;
        secondNumber = Double.parseDouble(jTxtDisplay.getText());
        
        switch (operations) {
            case "+":
                result = firstNumber + secondNumber;
                answer = String.format("%.1f", result);
                jTxtDisplay.setText(answer);
                break;
            case "-":
                result = firstNumber - secondNumber;
                answer = String.format("%.1f", result);
                jTxtDisplay.setText(answer);
                break;
            case "*":
                result = firstNumber * secondNumber;
                answer = String.format("%.1f", result);
                jTxtDisplay.setText(answer);
                break;
            case "/":
                result = firstNumber / secondNumber;
                answer = String.format("%.1f", result);
                jTxtDisplay.setText(answer);
                break;
            case "%":
                result = (firstNumber*secondNumber)/100;
                answer = String.format("%.1f", result);
                jTxtDisplay.setText(answer);
                break;
            default:
                jTxtDisplay.setText("[Erro]Inv√°lido");
                break;
        }
    }//GEN-LAST:event_jBtnEqualsActionPerformed

